CREATE TABLE `handypro`.`prosignup` (`id` INT(100) NOT NULL AUTO_INCREMENT , `business_name` VARCHAR(100) NOT NULL ,`business_category` VARCHAR(100) NOT NULL, `email` VARCHAR(100) NOT NULL , `phone_number` VARCHAR(100) NOT NULL , `local_area` VARCHAR(100) NOT NULL , `local_address` VARCHAR(100) NOT NULL , `pass` VARCHAR(100) NOT NULL , `description` VARCHAR(100) NOT NULL, `rating` VARCHAR(100) NOT NULL, PRIMARY KEY (`id`)) ENGINE = InnoDB;